const Header = ({ course }) => {
  return (<h1>{course}</h1>)
}

const Content = () => {
  return (
    <div>
      <Part part={cour} exercise={10} />
      <Part part="Using props to pass data" exercise={7} />
      <Part part="State of a component" exercise={14} />
    </div>

  )
}

const Part = ({ part, exercise }) => {
  return (<p>{part} {exercise}</p>)
}

const Total = ({ exercise1, exercise2, exercise3 }) => {
  return (<p>Number of exercises {exercise1 + exercise2 + exercise3}</p>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }

  return (
    <div>
      <Header course={course.name} />
      <Content parts={course}/>
      <Total exercise1={10} exercise2={7} exercise3={14} />
    </div>
  )
}

export default App
